                <ul>
                    {prayerTimes.map((dayData) => (
                        <li key={dayData.date.readable}>
                            <strong>Date:</strong> {dayData.date.readable}
                            <ul>
                                {Object.keys(dayData.timings).map((key) => (
                                    <li key={key}>
                                        <strong>{key}:</strong> {dayData.timings[key]}
                                    </li>
                                ))}
                            </ul>
                        </li>
                    ))}
                </ul>
            )}
        </div>
    
    )
};
export default PrayerTimings;
