import React, { useEffect, useState } from "react";
import axios from "axios";
import "./Login.css";



function Login() {
  const [login,setLogin]=useState({
    Username:"",
    password:""
  });
  let handleChange=(e)=>{
    e.preventDefault();
    const {id,value}=e.target;
    setLogin({...login,[id]:value});

}
  const getLogin=async(e)=>{
    e.preventDefault();
        
        try {
             const response= await axios.post("https://localhost:7221/User/login",{
              Username: login.Username,
              Password: login.password,

            })

            if(response.status===200){
                alert("Successful");
            }
            else{
                alert("Signup failed: ${response.data}")
            } 
            
        } catch (error) {
            console.error("Error:", error);
            alert('Please try again.');
        }
        //reset input boxes
        setLogin({
            Username:"",
            Password:"",
        });
  }

  useEffect(()=>{
    getLogin();

  },[])

  let submitForm = (e) => {
   e.preventDefault();
    alert("hello");
  };

  return (
    <div className="login-container">
      <h1>Login</h1>
      <form className="login" onSubmit={submitForm}>
        <label>Username</label>
        <input onChange={handleChange} id="Username" value={login.Username} type="email" required />
        <label>Password</label>
        <input  onChange={handleChange} id="password" value={login.password} type="password" minLength={8} required />

        <button>Login</button>
        <p>Forgot Password?</p>
      </form>
    </div>
  );
}
export default Login;
